#include <unistd.h>

#include <sc.h>
#include <sc_fs.h>

int _isatty(int file)
{
    if ((file < 0) || (file > SC_FS_FD_MAX)) {
        errno = EBADF;
        return 0;
    }

    switch (file) {
    case STDOUT_FILENO:
    case STDIN_FILENO:
    case STDERR_FILENO:
        return 1;
    }

    errno = ENOTTY;

    return 0;
}

