 /****************************************************************************
 [pf_exfat_config.h] - Configurations of "exFAT Support"
 ****************************************************************************/
#ifndef PF_EXFAT_CONFIG_H
#define PF_EXFAT_CONFIG_H

#ifndef PARTS_CFG
#include "parts.cfg"
#endif

/***************************************************************************
  CONFIGURATIONS
 ***************************************************************************/
#if USE_EBINDER
  /*
   * In eBinder environment, The configurations are defined in
   * 'eb_pf_exfat_config.h' which is automatically generated by
   * eBinder Configurator.
   */
#include "prfile2/eb_pf_exfat_config.h"

#else /* ! USE_EBINDER */

  /*
   * If set, setting "PercentInUse" field for exFAT.
   * Default : 1
   */
#define PF_EXFAT_SET_PERCENT_IN_USE                 0

  /*
   * If set, updating the VolumeDirty bit in BSS.
   * Default : 1
   */
#define PF_EXFAT_UPDATE_VOLUME_DITRY                0

  /*
   * If set, checking the VolumeDirty bit in BSS.
   * Default : 1
   */
#define PF_EXFAT_CHECK_VOLUME_DITRY                 0

  /*
   * If set, using exFAT Format Function.
   * Default : 1
   * NOTES:
   *  - When a driver formats, this parameter sets 0.
   */
#define PF_EXFAT_FORMAT_SUPPORT                     1

  /*
   * If set, when creats a new file (or new directory),
   * always use FAT Chain.
   * Default : 0
   */
#define PF_EXFAT_USE_FAT_CHAIN                      1

#endif /* USE_EBINDER */

#endif /* PF_EXFAT_CONFIG_H */
